package com.example.websocketchat.Entities;

import android.graphics.Bitmap;
import android.util.Log;

import com.example.websocketchat.Util.ChatActivity;
import com.google.gson.Gson;
import com.google.gson.reflect.TypeToken;

import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.lang.reflect.Type;
import java.util.List;

/**This call is equivalent to JSON.parse in Javascript**/
public class JsonToMsg {
    private Message msg;
    private Message msg1;

    public Message JsonToMsg(String json){

        Gson gson = new Gson();
        Type type = new TypeToken<List<String>>(){}.getType();
        //Convert Json String to back to List<String> which Message is embedded.
        List<String> fromJson = gson.fromJson(json, type);

        int v1=Integer.valueOf(fromJson.get(0));
        String v2=fromJson.get(1);
        String v3=fromJson.get(2);
        int v4=Integer.valueOf(fromJson.get(3));
        String v5=fromJson.get(4);
        String v6=fromJson.get(5);
        String v7=fromJson.get(6);

        switch(v1){
            case Message.TEXT_MESSAGE:
                msg=new Message(v1,v2,v3,v4,v5,v6,v7);

                break;
            case Message.IMAGE_MESSAGE:

                msg=new Message(v1,null,v3,v4,v5,v6,v7);


                Type string = new TypeToken<String>(){}.getType();
                String base64ImgString=gson.fromJson(v2,string); //Convert v2 from json string back to base64 string
                //String base64ImgString=v2; //v2 is already in base64 string so there is no need to convert from Json string
                //String base64ImgString="";
                //String base64ImgString1="";

                Bitmap decodedImg=null;
                /**Convert base64 image stored in v2 back to bitmap then byteArray**/
                    //String testJson=gson.toJson(base64ImgString1);
                    //String testbase64=gson.fromJson(testJson,string);


                decodedImg= msg.base64StringToBitmap(base64ImgString);

                msg.setByteArray(bitmapToByteArray(decodedImg));





                break;
            case Message.USERLIST_MESSAGE:
                msg=new Message(v1,v2,v3,v4,v5,v6,v7);
                Type stringList = new TypeToken<List<String>>(){}.getType();
                List<String> userList=gson.fromJson(v2,stringList); //Convert userlist from JSON string back to string
                msg.setUserList(userList);
                break;

            case Message.LOGIN_MESSAGE:
                msg=new Message(v1,v2,v3,v4,v5,v6,v7);
                break;

            case Message.SIGNUP_MESSAGE:
                msg=new Message(v1,v2,v3,v4,v5,v6,v7);

                break;

            case Message.BROADCAST_MESSAGE:
                msg=new Message(v1,v2,v3,v4,v5,v6,v7);
                break;

        }


        return msg;
    }
    public static byte[] bitmapToByteArray(Bitmap bitmap){

        ByteArrayOutputStream baos = new ByteArrayOutputStream();
        bitmap.compress(Bitmap.CompressFormat.JPEG, 100, baos);
        byte[] b = baos.toByteArray();

        return b;
    }
}

